	assert.Nil(t, err)

	repo, _ := chain.NewRepository(db, b0)

	state := state.New(db, trie.Root{Hash: b0.Header().StateRoot()})
	rt := runtime.New(repo.NewChain(b0.Header().ID()), state, &xenv.BlockContext{}, thor.NoFork)

